package quiz.app;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class Login extends JFrame implements ActionListener {    JTextField text;    JButton Next, back;    Login(){        getContentPane().setBackground(Color.CYAN);        setLayout(null);        ImageIcon i1 = new ImageIcon(ClassLoader.getSystemResource("icons/hehe.png"));        Image i = i1.getImage().getScaledInstance(1146,1080,Image.SCALE_DEFAULT);        ImageIcon i2 = new ImageIcon(i);        JLabel image = new JLabel(i2);        image.setBounds(-10,-40,1146,1080);        add(image);        JLabel name = new JLabel("Enter Your Name");        name.setBounds(1195,230,300,30);        name.setFont(new Font("Sans Serif", Font.BOLD,35));        name.setForeground(new Color(2,15,31));        add(name);        text = new JTextField();        text.setBounds(1195,270,300,30);        text.setFont(new Font("Sans Serif", Font.BOLD,20));        add(text);        Next = new JButton("Next");        Next.setBounds(1370,310,120,40);        Next.setBackground(new Color(1,50,32));        Next.setForeground(Color.WHITE);        Next.addActionListener(this);        add(Next);        back = new JButton("Exit");        back.setBounds(1195,310,120,40);        back.setBackground(new Color(139,0,0));        back.setForeground(Color.WHITE);        back.addActionListener(this);        add(back);        setSize(1920,1080);        setLocation(0,0);        setUndecorated(true);        setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == Next){            String name = text.getText();            setVisible(false);            new Rules(name);        } else if (e.getSource() == back) {            System.exit(50);        }    }    public static void main(String[] args) {        new Login();    }}