package quiz.app;import javax.swing.*;import java.awt.*;import java.awt.event.ActionEvent;import java.awt.event.ActionListener;public class Rules extends JFrame implements ActionListener {    JButton start, back;    String name;    Rules(String name){        this.name = name;        JLabel heading = new JLabel(" Kindly read the rules " + name + " ," + " Thank you");        heading.setBounds(50,90,1500,200);        heading.setFont( new Font("Sans Serif", Font.BOLD, 70));        heading.setForeground(new Color(0,255,255));        add(heading);        JLabel rules = new JLabel();        rules.setBounds(70,-90,1500,1080);        rules.setFont( new Font("Sans Serif", Font.PLAIN, 25));        rules.setForeground(new Color(255,255,255));        rules.setText(                "<html>"+                        "1. You have 20 seconds to answer each question." + "<br><br>" +                        "2. There are a total 10 questions. " + "<br><br>" +                        "3. You only have one chance to use help, if you click the help button 2 of the choices will " +                        "be remove." + "<br><br>" +                        "4. The questions are all related to Java." + "<br><br>" +                        "5. You have a  4 options each questions." + "<br><br>" +                        "6. You can only submit when you are done." + "<br><br>" +                        "<html>"        );        add(rules);        back = new JButton("Back");        back.setBounds(300,680,100,50);        back.setBackground(new Color(2,15,31));        back.setForeground(Color.CYAN);        back.addActionListener(this);        add(back);        start = new JButton("Start");        start.setBounds(450,680,100,50);        start.setBackground(new Color(2,15,31));        start.setForeground(Color.CYAN);        start.addActionListener(this);        add(start);        ImageIcon i1 = new ImageIcon(ClassLoader.getSystemResource("icons/mwehe.png"));        Image i = i1.getImage().getScaledInstance(1920,1080,Image.SCALE_DEFAULT);        ImageIcon i2 = new ImageIcon(i);        JLabel image = new JLabel(i2);        image.setBounds(0,0,1920,1080);        add(image);        setSize(1920,1080);        setLocation(0,0);        setLayout(null);        setUndecorated(true);        setVisible(true);    }    @Override    public void actionPerformed(ActionEvent e) {        if (e.getSource() == start){            setVisible(false);            new Quiz(name);        }else {            setVisible(false);            new Login();        }    }    public static void main(String[] args) {        new Rules("User");    }}